mapper.select:
select title, u.name, content, hit, reg_date "date", user_no userNo
from rboard b, users u
where b.user_no = u.no and b.no = #{no}

mapper.update:



			update      rboard
            set         hit = hit+1
            where       no = #{no}




update		rboard
set			title = #{title},
			content = #{content}
where		no = #{no}
